const React = require('react');
const ReactDOM = require('react-dom');

var AutoComposerLogic = require('./autocomposer-logic');
var AcLogic = new AutoComposerLogic.AutoComposerLogic();

var AutoComposerMelody = require('./autocomposer-melody');
var AcMelody = new AutoComposerMelody.AutoComposerMelody();

var acMidi = require('./autocomposer-midi');
var AcMidi = new acMidi.AutoComposerMidi();

var AcButton = require('./react/ac-button');
var AcToggleButton = require('./react/ac-toggle-button');
var AcTextField = require('./react/ac-text-field');
var AcTextArea = require('./react/ac-textarea');
var AcSelect = require('./react/ac-select');
var AcRadioSet = require('./react/ac-radioset');
var AcCheckbox = require('./react/ac-checkbox');
var ErrorMessage = require('./react/error-message');
var StatusOutput = require('./react/status-output')

var HelpPanel = require('./react/help-panel');
var DebugPanel = require('./react/debug-panel');
var OutputPanel = require('./react/output-panel');
var ControlPanel = require('./react/control-panel');



function AcInputException(message) {
   this.message = message;
   this.name = 'AcInputException';
}



/**
* The core React component for the web app.
*/
class AutoComposer extends React.Component {
  constructor(props) {
    super(props);

    this.state = {
      showHelp: false,
      showControls: false,
      showOutput: false,
      showError: false,

      debugMode: false,
      controlsDisabled: true, // While this is a mess, no need to show it.

      chordProgressionRaw: "",
      chordProgressionChanged: false,

      errorMessage: "",

      // This becomes true whenever we have a chord progression change, and the correct button is clicked.
      // Returns to false after output finishes rendering
      allowMelodyGeneration: "",
      melodyLoaded: false
    };

    /**
    * @type {Object} - Data store. Any changes to this data store will not cause an update.
    */
    this.store = {
      melodies: [],
      chordProgressionPlaceholder: AcLogic.INITIAL_PROGRESSION,
    };

    this.handleChange = this.handleChange.bind(this);
    this.generateMelodies = this.generateMelodies.bind(this);
    this.outputFinishCallback = this.outputFinishCallback.bind(this);
    this.callbackChangeState = this.callbackChangeState.bind(this);
    this.loadMusic = this.loadMusic.bind(this);

    this.playMelody = this.playMelody.bind(this);
    this.playMelodySolo = this.playMelodySolo.bind(this);
    this.stopMusic = this.stopMusic.bind(this);
    this.downloadMidi = this.downloadMidi.bind(this);
  }

  /**
  * Changes app state. Meant to be called from child components
  * @param {string} stateKey - App state to change
  * @param {number|string|Object} newState - Assigns this to the state
  * @param {boolean} [useDataStore] - If true, the data store is changed instead of the state object.
  */
  callbackChangeState(stateKey, newState, useDataStore) {
    console.debug("app callbackChangeState()");

    if(useDataStore) {
      this.store[stateKey] = newState;
    } else {
      var stateObj = function() {
        var returnObj = {};

        returnObj[stateKey] = newState;

        return returnObj;
      }.bind(this)();

      this.setState(stateObj);
    }
  }

  /**
  * Changes app state. Meant to be called from child components.
  * Older function, compared to callbackChangeState()
  * @param {Object} event - React event
  */
  handleChange(event) {
    console.debug("app handleChange()");
    var stateObj = function() {
      var stateKey = this.target.dataset["stateKey"];
      var returnObj = {};

      if(this.target.type === "checkbox") {
        returnObj[stateKey] = this.target.checked;
      } else if(this.target.type === "button") {
        returnObj[stateKey] = this.target.dataset["currentState"] === "true";
      } else {
        returnObj[stateKey] = this.target.value;

        if(stateKey === "chordProgressionRaw") {
          returnObj["chordProgressionChanged"] = true;
        }
      }

      return returnObj;
    }.bind(event)();

    this.setState(stateObj);
  }

  /**
  * Loads melody data into the App. Meant to be used as a callback from the results table.
  * @param {Object} event - React event
  */
  loadMusic(event) {
    console.debug("app loadMusic()");
    var melodyString = event.target.dataset["payload"];
    var melodiesData = melodyString.split(";");

    var melody1 = melodiesData[0].split(",");
    var melody2 = melodiesData[1].split(",");
    var melody3 = melodiesData[2].split(",");

    var melodies = [melody1, melody2, melody3];
    this.store.melodies = melodies;
    this.setState({melodyLoaded: true})
  }

  /**
  * Generates the melodies for the given chord progression.
  * @param {Object} event - React event
  */
  generateMelodies(event) {
    var chordProgression = this.state.chordProgressionRaw.trim().split(" ");

    if(!this.state.chordProgressionChanged) {
      // Chord progression hasn't changed. No need to continue.
      console.debug("[AutoComposer.generateMelodies()] Chord progression hasn't changed. No generation for you.");
      return;
    }

    try {
      if(this.state.chordProgressionRaw == null || this.state.chordProgressionRaw == "") {
        throw new AcInputException('Chord input is empty!');
      }

      chordProgression.forEach(function(currentChordInput) {
        if(!AcLogic.isValidText(currentChordInput)) {
          throw new AcInputException('Chord input \'' + currentChordInput + '\' is not formatted properly! You should check the chord dictionary in the Help! section.');
        }
      });

      if(chordProgression.length < 2) {
        throw new AcInputException('You need to enter more chords. Two chords in a row is a completely valid input.');
      }

      this.setState({showError: false, showOutput: true, allowMelodyGeneration: true});
    } catch(exc) {
      console.warn("[AutoComposer.generateMelodies()] " + exc.message + "\nError Type = " + exc.name);
      this.setState({showError: true, errorMessage: exc.message});
    }
  }

  /**
  * Plays the loaded melody
  * @param {Object} event - React event
  */
  playMelody(event) {
    if(this.store.melodies.length > 0) {
      AcMidi.playMelodyWithAccompaniment(this.store.melodies[0], this.store.melodies[1], this.store.melodies[2]);
    }
  }

  /**
  * Plays the loaded melody by itself
  * @param {Object} event - React event
  */
  playMelodySolo(event) {
    if(this.store.melodies.length > 0) {
      AcMidi.playMelodySolo(this.store.melodies[0]);
    }
  }

  /**
  * Stops all music currently playing.
  * @param {Object} event - React event
  */
  stopMusic(event) {
    AcMidi.stopPlayback();
  }

  /**
  * Creates and sends MIDI data to the user.
  * @param {Object} event - React event
  */
  downloadMidi(event) {
    if(this.store.melodies.length > 0) {
      //download MIDI file
      var dataString = AcMidi.buildMelodyMidiWithAccompaniment(this.store.melodies[0], this.store.melodies[1], this.store.melodies[2]);
      var timestamp = new Date().valueOf().toString().slice(-8)

      var fileName = "autocomposer-" + timestamp + "_" + this.store.melodies[0].join("-");
      download(dataString, fileName, "audio/midi");
    }
  }

  /**
  * Callback that runs when output panel is finished rendering.
  * Prevents melody generation until the user enters a new progression.
  * @param {Object} event - React event
  */
  outputFinishCallback() {
    this.setState({allowMelodyGeneration: false, chordProgressionChanged: false});
  }

  render() {
    var chordProgressionArray = this.state.chordProgressionRaw.split(" ");
    // Assume that we have an empty body tag.
    return (
      <div id="app-container" className="root-panel">
        <div id="main-control-panel" className="ac-panel">
          <h3>AutoComposer</h3>

          <div className="panel-row has-labels">
            <AcTextArea inputKey="chordProgressionRaw" addClass="double-height" value={this.state.chordProgressionRaw} placeholder={this.store.chordProgressionPlaceholder} onChange={this.handleChange} />
            <AcButton inputKey="generateMelodies" inputLabel="Generate!" wrapperAddClass="square" addClass="double-height blue" onClick={this.generateMelodies} isActive={!this.state.chordProgressionChanged}/>
          </div>

          <div className="panel-row">
            <AcToggleButton inputKey="showControls" icon="cog" initialState={this.state.showControls} onClickHandler={this.callbackChangeState} disabled={this.state.controlsDisabled} />
            <AcToggleButton inputKey="showHelp" icon="question" initialState={this.state.showHelp} onClickHandler={this.callbackChangeState} />
          </div>

          <div className="panel-row">
            <AcButton inputKey="generateMelodies" icon="play" addClass="green" wrapperAddClass="flex-lg" onClick={this.playMelody} disabled={!this.state.melodyLoaded}/>
            <AcButton inputKey="generateMelodies" icon="play" addClass="green" inputLabel=" (Solo)" wrapperAddClass="flex-sm" onClick={this.playMelodySolo} disabled={!this.state.melodyLoaded}/>
            <AcButton inputKey="generateMelodies" icon="stop" addClass="red" wrapperAddClass="flex-sm" onClick={this.stopMusic} disabled={!this.state.melodyLoaded}/>
            <AcButton inputKey="generateMelodies" icon="download" wrapperAddClass="flex-sm" onClick={this.downloadMidi} disabled={!this.state.melodyLoaded}/>
          </div>

          <div className="panel-row">
            <StatusOutput inputKey="status-output" value="status output..." />
          </div>

          <ErrorMessage isShown={this.state.showError} errorMessage={this.state.errorMessage} />

          <DebugPanel isHidden={!this.state.debugMode} debugData={JSON.stringify(this.state, null, 2)}/>
        </div>

        <ControlPanel isShown={this.state.showControls} />

        <HelpPanel isShown={this.state.showHelp} />

        <OutputPanel isShown={this.state.showOutput} chordProgression={chordProgressionArray} allowMelodyGeneration={this.state.allowMelodyGeneration} outputCallback={this.outputFinishCallback} loadMelody={this.loadMusic} />

      </div>
    );
  }
}

ReactDOM.render(<AutoComposer />, document.getElementById('react-root'));

var range = require('tonal-range');
var chord = require('tonal-chord');
var note = require('tonal-note');

/**
 * Encapsulates data and musical logic to be used by the application
 */
class AutoComposerLogic {
  constructor() {
    this.DEFAULT_LOWER_LIMIT = "Db4";
    this.DEFAULT_UPPER_LIMIT = "G#5";

    this.ACCOMPANIMENT_LOWER_LIMIT = "Gb2";
    this.ACCOMPANIMENT_UPPER_LIMIT = "C#4";

    this.BASS_LOWER_LIMIT = "E1";
    this.BASS_UPPER_LIMIT = "F2";

    this.INITIAL_PROGRESSION = "G Em C D",
    this.NUM_MELODIES_LIMIT = 100; // Number of melodies that the user sees.
    this.NUM_GENERATIONS_LIMIT = 100000; // Number of melody generation attempts that the program will make.
    this.CHORDS_LIMIT = 4;
  }

  /**
  * Returns all the chord types available for use.
  * @return {string[]} - chord types available for use
  */
  getChordDictionary() {
    return chord.names();
  }

  /**
  * Filters out melodies that have a range larger than an octave.
  * @param {string} melodyString - String representing the melody
  * @return {boolean} - Returns false if the melody has a range larger than one octave. Returns true otherwise
  */
  filterMelodyRange(melodyString) {
    var totalRange = range.numeric(melodyString);
    var highest = Math.max.apply(null, totalRange);
    var lowest = Math.min.apply(null, totalRange);

    return highest - lowest <= 12;
  }

    /**
    * Parses text, and determines if user input represents a valid note/chord.
    * @param {string} input - value given by the user
    * @return {boolean} - a true value means the string can be used by the rest of the program.
    */
  isValidText(input) {
    var isChord = chord.isKnownChord(input) ? true : false;
    var isNote = note.name(input) ? true : false;

    return isChord || isNote;
  }

    /**
    * Converts ASCII accidentals to Unicode accidentals in HTML
    * @param {string} input - text with ASCII accidentals
    * @return {string} - text with Unicode accidentals in HTML
    */
  convertAsciiAccidentalsToHtml(input) {
    var out = input.replace(/([A-G0-9])b/g, '$1&#9837;');
    out = out.replace(/([A-G0-9])#/g, '$1&#9839;');
    out = out.replace(/([A-G0-9])o/g, '$1&‌deg;');

    return out;
  }

    /**
    * Converts ASCII accidentals to Unicode accidentals
    * @param {string} input - text with ASCII accidentals
    * @return {string} - text with Unicode accidentals
    */
  convertAsciiAccidentalsToText(input) {
    var out = input.replace(/([A-G0-9])b/g, '$1♭');
    out = out.replace(/([A-G0-9])#/g, '$1♯');
    out = out.replace(/([A-G0-9])o/g, '$1°');

    return out;
  }

    /**
    * Converts Unicode accidentals to ASCII
    * @param {string} input - text with Unicode accidentals
    * @return {string} - text with ASCII accidentals
    */
  convertAccidentalsToAscii(input) {
    var out = input.replace(/([A-G0-9])♭/g, '$1b');
    out = out.replace(/([A-G0-9])♯/g, '$1#');
    out = out.replace(/([A-G0-9])°/g, '$1o');

    return out;
  }

};

exports.AutoComposerLogic = AutoComposerLogic;

var tonal = require('tonal');
var ChordUnit = require('./chord-unit');
var MelodyUnit = require('./melody-unit');

var AutoComposerLogic = require('./autocomposer-logic');
var AcLogic = new AutoComposerLogic.AutoComposerLogic();

/**
 * Creates melodies from a given chord progression
 */
class AutoComposerMelody {
  /**
  * @param {string[]} chordProgression - array of chord symbols
  * @param {string} lowerLimit - lower boundary note (in scientific notation)
  * @param {string} upperLimit - upper boundary note (in scientific notation)
  */
  constructor(chordProgression, lowerLimit, upperLimit) {
    /** @type {string[]} */
    this.chordProgression = chordProgression || AcLogic.INITIAL_PROGRESSION;
    /** @type {string} */
    this.lowerLimit = lowerLimit || AcLogic.DEFAULT_LOWER_LIMIT;
    /** @type {string} */
    this.upperLimit = upperLimit || AcLogic.DEFAULT_UPPER_LIMIT;
  }

    /**
    * For a given note, find its lowest instance in the specified range.
    * @private
    * @param {string} pitch - pitch class
    * @param {string} lowerLimit - note (written in scientific notation)
    * @param {string} upperLimit - note (written in scientific notation)
    * @return {string[]} - an array of notes (written in scientific pitch)
    */
  _getLowestNoteInRange(pitch, lowerLimit, upperLimit) {
    var chordTonesInRange = tonal.range.pitchSet(pitch, [lowerLimit, upperLimit]);
    return chordTonesInRange[0];
  }

    /**
    * For a given array of chord tones, remove the specified pitches.
    * @private
    * @param {string[]} pitchArray - pitches to remove
    * @param {string[]} chordTones - chord tones
    * @return {string[]} - the remaining chord tones
    */
  _removePitchesFromChordTones(pitchArray, chordTones) {
    var indexToRemove;
    pitchArray.forEach(function(pitch) {
      indexToRemove = chordTones.indexOf(pitch);
      if (indexToRemove > -1) {
        chordTones.splice(indexToRemove, 1);
      }
    });
    return chordTones;
  }

    /**
    * For a given MelodyUnit, get an accompaniment for it.
    * @private
    * @param {MelodyUnit} melodyUnit - melody that needs accompaniment
    * @return {string[]} - array of strings, each representing one or more notes to play under each melodic note.
    */
  getAccompaniment(melodyUnit) {
    // Omit root note, and maybe avoid doubling the top note as well.
    var noteArray = [], chordNotes, currentChord, bassPitch, topPitch;

    for(var i = 0; i < melodyUnit.chordProgression.length; i++) {
      currentChord = melodyUnit.chordProgression[i];
      bassPitch = tonal.chord.parse(currentChord)["tonic"];
      topPitch =  tonal.note.pc(melodyUnit.melodyNotes[i]);

      chordNotes = tonal.chord.notes(currentChord);
      chordNotes = this._removePitchesFromChordTones([bassPitch, topPitch], chordNotes);

      for(var j = 0; j < chordNotes.length; j++) {
        chordNotes[j] = this._getLowestNoteInRange(chordNotes[j], AcLogic.ACCOMPANIMENT_LOWER_LIMIT, AcLogic.ACCOMPANIMENT_UPPER_LIMIT);
      }
      noteArray.push(chordNotes.join(" "));
    }

    return noteArray;
  }

    /**
    * For a given MelodyUnit, return a basic bass line consisting only of root notes.
    * @private
    * @param {MelodyUnit} melodyUnit - melody that needs a bassline
    * @return {string} - string representing a bassline.
    */
  getBasicBassLine(melodyUnit) {
    var noteArray = [], currentChord, bassPitch, bassNote;

    // return all the lowest root notes for the progression.
    for(var i = 0; i < melodyUnit.chordProgression.length; i++) {
      currentChord = melodyUnit.chordProgression[i];
      bassPitch = tonal.chord.parse(currentChord)["tonic"];
      bassNote = this._getLowestNoteInRange(bassPitch, AcLogic.BASS_LOWER_LIMIT, AcLogic.BASS_UPPER_LIMIT);

      noteArray.push(bassNote);
    }

    return noteArray;
  }

    /**
    * For a given chord, get all the chord tones between the upper and lower limits.
    * @private
    * @param {string} chord - chord symbol
    * @param {string} lowerLimit - note (written in scientific notation)
    * @param {string} upperLimit - note (written in scientific notation)
    * @return {string[]} - an array of notes (written in scientific pitch)
    */
  getAllChordTones(chord, lowerLimit, upperLimit) {
    var chordTones = tonal.chord(chord);
    var chordTonesInRange = tonal.range.pitchSet(chordTones, [lowerLimit, upperLimit]);

    for(var i = 0; i < chordTonesInRange.length; i++) {
      for(var j = 0; j < chordTones.length; j++) {
        // Fixing pesky issue where D7 was returned as "D Gb A C" instead of "D F# A C"
        // If the current chord tone is enharmonic with the note from the pitch set,
        // Override it with the chord tone.
        if(tonal.note.pc(chordTonesInRange[i]) != chordTones[j]
          && tonal.note.enharmonics(chordTones[j]).indexOf(tonal.note.pc(chordTonesInRange[i])) > -1) {
          chordTonesInRange[i] = chordTones[j] + tonal.note.oct(chordTonesInRange[i]);
        }
      }
    }

    return chordTonesInRange;
  }

    /**
    * For a given chord symbol, creates a ChordUnit object
    * @private
    * @param {string} chord - chord symbol
    * @param {string} lowerLimit - note (in scientific notation)
    * @param {string} upperLimit - note (in scientific notation)
    * @return {ChordUnit}
    */
  buildChordUnit(chord, lowerLimit, upperLimit) {
    var chordTonesInRange = this.getAllChordTones(chord, lowerLimit, upperLimit);
    var chordUnit = new ChordUnit.ChordUnit(chord, chordTonesInRange, null);
    return chordUnit;
  }

    /**
    * For a given melody, creates a MelodyUnit object
    * @private
    * @param {string[]} chordProgression - a chord progression
    * @param {string} melodyString - a melody (in scientific notation)
    * @return {MelodyUnit}
    */
  buildMelodyUnit(chordProgression, melodyString) {
    var arrMelody = melodyString.split(" ");
    var melodyUnit = new MelodyUnit.MelodyUnit(chordProgression, arrMelody);
    return melodyUnit;
  }

    /**
    * For a given chord progression and melody, generate a series of melodies that fit over the progression
    * @private
    * @param {string[]} chordProgression - a chord progression
    * @param {string[]} rawMelodies - a string representing the melody
    * @param {Object} options - if true, generated melodies will be sorted, with smoothest melodies coming first.
    * @param {boolean} options.sort - if true, generated melodies will be sorted, with smoothest melodies coming first.
    * @param {number} options.limit - limits the output to a set number.
    * @return {MelodyUnit[]} - a list of ChordUnit objects.
    */
  buildMelodyUnitList(chordProgression, rawMelodies, options) {
    var melodyUnits = [];
    var haxThis = this;

    rawMelodies.forEach(function(rawMelody) {
      melodyUnits.push(haxThis.buildMelodyUnit(chordProgression, rawMelody));
    });

    if(options) {
      if(options.sort) {
        melodyUnits.sort(function(a, b) {
          return a.smoothness - b.smoothness;
        });
      }

      if(options.limit) {
        melodyUnits.splice(options.limit);
      }
    }

    console.log("[AutoComposerMelody.buildMelodyUnitList()] Returning list of " + melodyUnits.length + " melodies");
    return melodyUnits;
  }

    /**
    * For a given chord progression, generate a series of melodies that fit over the progression
    * @private
    * @param {string[]} chordProgression - chord symbols
    * @param {string} lowerLimit - lower limit (in scientific notation). Optional value.
    * @param {string} upperLimit - upper limit (in scientific notation). Optional value.
    * @return {ChordUnit[]} - a list of ChordUnit objects.
    */
  buildChordUnitList(chordProgression, lowerLimit, upperLimit) {
    if(lowerLimit == null) {
      lowerLimit = this.lowerLimit;
    }
    if(upperLimit == null) {
      upperLimit = this.upperLimit;
    }

    var chordUnitList = [];
    var chordTonesInRange;

    for(var i = chordProgression.length - 1; i >= 0; i--) {
      chordTonesInRange = this.getAllChordTones(chordProgression[i], lowerLimit, upperLimit);

      if(i === chordProgression.length) {
        chordUnitList[i] = new ChordUnit.ChordUnit(chordProgression[i], chordTonesInRange, null);
      } else {
        chordUnitList[i] = new ChordUnit.ChordUnit(chordProgression[i], chordTonesInRange, chordUnitList[i + 1]);
      }
    }

    return chordUnitList;
  }

    /**
    * Recursive function that adds new notes to the previous notes passed into it.
    * On the first call of this function, melodyList should be null.
    * @private
    * @param {ChordUnit} chordUnit - the ChordUnit for the next chord
    * @param {?string[]} melodyList - list of existing melodies
    * @param {Object} options - if true, generated melodies will be filtered
    * @param {boolean} options.filtered - if true, generated melodies will be filtered
    * @param {boolean} options.rawOutput - if true, generated melodies will be given as strings
    * @return {string[]} - a list of melodies. Each element is a string represeting a melody. Each melody string is written as a series of pitches delimited by a space.
    */
  getMelodiesCore(chordUnit, melodyList, options) {
    var returnList = [];
    var chordTones = chordUnit.chordTones;
    var rawMelody, newMelody;
    var haxThis = this;

    if(melodyList) {
      // We're somewhere along the middle of the chain.
      melodyList.forEach(function(currentMelody) {
        chordTones.forEach(function(currentChordTone) {
          newMelody = currentMelody + " " + currentChordTone;

          if(options.filtered) {
            if(AcLogic.filterMelodyRange(newMelody)) {
              returnList.push(newMelody);
            }
          } else {
            returnList.push(newMelody);
          }
        });
      });
    } else {
      // This is the beginning of the chain.
      melodyList = chordUnit.chordTones;
      returnList.push.apply(returnList, melodyList);
    }

    if(chordUnit.nextChordUnit) {
      // We're somewhere before the end of the chain.
      return this.getMelodiesCore(chordUnit.nextChordUnit, returnList, options);
    } else {
      // End of the chain.
      console.log("[AutoComposerMelody.getMelodiesCore()] Returning list of " + returnList.length + " melodies");
      return returnList;
    }
  }

    /**
    * For a given chord progression, generate a series of melodies that fit over the progression.
    * @param {string[]} chordProgression - chord progression given by user
    * @return {MelodyUnit[]} - an array of notes (written in scientific pitch)
    */
  getAllMelodies(chordProgression) {
    var chordUnitList = this.buildChordUnitList(chordProgression, this.lowerLimit, this.upperLimit);
    var melodies = this.getMelodiesCore(chordUnitList[0], null, {filtered: false});

    var melodyUnits = [];
    var haxThis = this;
    melodies.forEach(function(rawMelody) {
      melodyUnits.push(haxThis.buildMelodyUnit(chordProgression, rawMelody));
    });

    return melodyUnits;
  }

    /**
    * For a given chord progression, generate a series of melodies that fit over the progression.
    * @param {string[]} chordProgression - chord progression given by user
    * @return {string[]} - an array of notes (written in scientific pitch)
    */
  getRawMelodies(chordProgression) {
    var chordUnitList = this.buildChordUnitList(chordProgression, this.lowerLimit, this.upperLimit);
    var melodies = this.getMelodiesCore(chordUnitList[0], null, {filtered: true, rawOutput: true});

    return melodies;
  }

    /**
    * For a given chord progression, generate a series of melodies that fit over the progression.
    * Currently, the melodies are sorted by smoothness, and are limited to the first 100 melodies.
